import React, { useState } from 'react';


function Quiz() {
  const [answers, setAnswers] = useState({});
  const [submitted, setSubmitted] = useState(false);
  const [showThankYou, setShowThankYou] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    setSubmitted(true);
    setShowThankYou(true);
    // Add your form submission logic here
  };

  const handleAnswerChange = (questionNumber, answer) => {
    setAnswers({ ...answers, [questionNumber]: answer });
  };

  return (
    <div>
      <h1>Mental Health Quiz</h1>
      {!submitted ? (
        <form onSubmit={handleSubmit}>
          <div>
            <p>Question 1: How often do you feel sad or hopeless?</p>
            <label>
              <input
                type="radio"
                name="question1"
                value="Never"
                onChange={() => handleAnswerChange('question1', 'Never')}
              />
              Never
            </label>
            <label>
              <input
                type="radio"
                name="question1"
                value="Rarely"
                onChange={() => handleAnswerChange('question1', 'Rarely')}
              />
              Rarely
            </label>
            <label>
              <input
                type="radio"
                name="question1"
                value="Sometimes"
                onChange={() => handleAnswerChange('question1', 'Sometimes')}
              />
              Sometimes
            </label>
            <label>
              <input
                type="radio"
                name="question1"
                value="Often"
                onChange={() => handleAnswerChange('question1', 'Often')}
              />
              Often
            </label>
          </div>
          <div>
            <p>Question 2: Do you have trouble concentrating in school or other activities?</p>
            <label>
              <input
                type="radio"
                name="question2"
                value="Never"
                onChange={() => handleAnswerChange('question2', 'Never')}
              />
              Never
            </label>
            <label>
              <input
                type="radio"
                name="question2"
                value="Rarely"
                onChange={() => handleAnswerChange('question2', 'Rarely')}
              />
              Rarely
            </label>
            <label>
              <input
                type="radio"
                name="question2"
                value="Sometimes"
                onChange={() => handleAnswerChange('question2', 'Sometimes')}
              />
              Sometimes
            </label>
            <label>
              <input
                type="radio"
                name="question2"
                value="Often"
                onChange={() => handleAnswerChange('question2', 'Often')}
              />
              Often
            </label>
          </div>
          <button type="submit">Submit</button>
        </form>
      ) : (
        <div>
          {showThankYou ? (
            <div>
              <p>Thank you for submitting the quiz!</p>
              <button onClick={() => setShowThankYou(false)}>Take the quiz again</button>
            </div>
          ) : (
            <div>
              {/* Add a message or a loading spinner here while the form is being submitted */}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default Quiz;
